import React, { useState } from 'react'
import "./home.css";
import Search from './search.jsx'

export default function Home(props) {

  const [filtred, setFiltered] = useState(props.products)

  const search = (term) => {
    setFiltered(props.products.filter(product => product.name.toLowerCase().includes
    (term.toLowerCase())))
  }

  return (
    <>
      <Search setSearch={search} />
      <div className='products'>
        <img className='productimg' src={props.setPaint} alt="Paint/" /> 
        <h1 className="productname">{props.setPainters}</h1>
        <p className="productprice">${this.props.setPaintPrice}</p>
      </div>
      </>
    )
  }
