import React from 'react';
import { withRouter } from "react-router-dom";
import { paint } from "./paint.js"

const Show= (props) => {
  const show = props.match.params.Showtime;
  const find = paint.find(paints =>
    paints.name === show) 
  return (
      <div className="context">
      <h1 className="name">{find.name}</h1>
      <p className="price">{find.price}</p>
      <p className="description">{find.description}</p>
      <img src={find.imgURL} />
      <a href={find.homepage}>
      <button>ShowMore</button> </a>
      </div>
    )
}
export default withRouter(Show);
